        "school": "abjuration",
        "spelllevel": "cleric/oracle 6, sorcerer/wizard 7, summoner 5, inquisitor 5, shaman 6, psychic 7, spiritualist 6, unchained summoner 6",
        "castingtime": "1 standard action",
