        "school": "transmutation",
        "spelllevel": "cleric/oracle 7, druid 7, sorcerer/wizard 7, witch 7, shaman 7",
        "castingtime": "10 minutes; see text",
        "components": "V, S",
        "costlycomponents": 0,
