        "name": "Detect Undead",
        "school": "divination",
        "spelllevel": "cleric/oracle 1, paladin 1, sorcerer/wizard 1, alchemist 1, inquisitor 1, shaman 1, psychic 1, mesmerist 1, spiritualist 1, medium 1",
        "castingtime": "1 standard action",
